async function initWeb3() {
  const providerOptions = {
    walletconnect: {
      package: WalletConnectProvider,
      options: {
        // Mikko's test key - don't copy as your mileage may vary
        infuraId: "9aa3d95b3bc440fa88ea12eaa4456161",
      },
    },
  };
  web3Modal = new Web3Modal({
    cacheProvider: false, // optional
    providerOptions, // required
    disableInjectedProvider: false, // optional. For MetaMask / Brave / Opera.
  });
  try {
    provider = await web3Modal.connect();
    provider.enable();
    flush();
  } catch (e) {
    console.log("Could not get a wallet connection", e);
    return;
  }
  // Subscribe to accounts change
  provider.on("accountsChanged", async (accounts) => {
    flush();
  });
  // Subscribe to chainId change
  provider.on("chainChanged", async (chainId) => {
    flush();
  });
  // Subscribe to networkId change
  provider.on("networkChanged", async (networkId) => {
    flush();
  });
}

async function flush() {
  web3 = new Web3(provider);
  chainId = await web3.eth.getChainId();
  console.log("chainId:" + chainId);

  if (chainId == 1) {
    uint = "ETH";
    // const contractAddress = "0x766b39d4b747ddcf78eab0daa447e9650e2c239d";
    // contract = new web3.eth.Contract(ABI, contractAddress);
    $(".network").html("Ethereum Mainnet");
    $(".network").css({
      background: "linear-gradient(90deg, blue, red)",
      "-webkit-text-fill-color": "transparent",
      "-webkit-background-clip": "text",
    });
    explorerUrl = "https://etherscan.io";
    //   $("#network").html("Ethereum Mainnet");
  } else if (chainId == 4) {
    uint = "ETH";
    const rainbiwWallProtocolContractAddress =
      "0xdab31242325B282ec101CfD595CF54ABe5E10aE2";
    rainbowWallProtocol = new web3.eth.Contract(
      RainbowWallProtocolABI,
      rainbiwWallProtocolContractAddress
    );
    explorerUrl = "https://rinkeby.etherscan.io";

    $(".network").html("Ethereum Rinkeby");
    $(".network").css({
      background: "linear-gradient(90deg, yellow, blue)",
      "-webkit-text-fill-color": "transparent",
      "-webkit-background-clip": "text",
    });
  } else if (chainId == 56) {
    // const contractAddress = "0x766b39d4b747ddcf78eab0daa447e9650e2c239d";
    // contract = new web3.eth.Contract(ABI, contractAddress);
    explorerUrl = "https://bscscan.com";
    uint = "BNB";
    $(".network").html("Binance Smart Chain Mainnet");
    $(".network").css({
      background: "linear-gradient(90deg, gold, red)",
      "-webkit-text-fill-color": "transparent",
      "-webkit-background-clip": "text",
    });
  } else {
    $(".network").html("Invalid Network");
    $(".network").css({
      background: "red",
      "-webkit-text-fill-color": "transparent",
      "-webkit-background-clip": "text",
    });
  }
  const accounts = await web3.eth.getAccounts();
  selectedAccount = accounts[0];
  let ensName = await getEnsName(selectedAccount);
  console.log(selectedAccount);
  $(".account").html(ensName);
  getSwapBarData();
}

async function showSymbolData(tokenId) {
  $(".contentView").find(".card").remove();
  $(".token-balance").html("");

  console.log(tokenId);
  let tokenAddress = await rainbowWallProtocol.methods
    .tokenList(tokenId)
    .call();
  appendContent(tokenId);
  let rainbowWallToken = new web3.eth.Contract(
    RainbowWallTokenABI,
    tokenAddress
  );
  let symbol = await rainbowWallToken.methods.symbol().call();
  $(".selecToken").html(symbol)
  let balance = await rainbowWallToken.methods
    .balanceOf(selectedAccount)
    .call();
  $(".token-balance").html(symbol + " amount: " + (balance / 1e18).toFixed(6));
  $(".swap").remove("disabled")
  $(".swap").html('<span class="text-rainbow">SWAP</span>')
}

async function getSwapBarData() {
  let ethBalance = await web3.eth.getBalance(selectedAccount);
  $(".uint-balance").html(uint + " amount: " + (ethBalance / 1e18).toFixed(6));
  $(".token-input").blur(async function () {
    $(".symbol").html("");
    let tokenId = $(this).val();
    console.log(tokenId);
    let tokenAddress = await rainbowWallProtocol.methods
      .tokenList(tokenId)
      .call();
    let rainbowWallToken = new web3.eth.Contract(
      RainbowWallTokenABI,
      tokenAddress
    );
    let symbol = await rainbowWallToken.methods.symbol().call();

    $(".symbol").html(symbol);
    $(".symbol").attr("onclick","showSymbolData("+tokenId+")");
  });
}

function getCard() {
  return $(
    '<div class="card shadow mb-4 card-center"><div class="card-header price-bar" ></div><div class="card-body"><p class="text-rainbow text-center"></p></div></div>'
  );
}

async function buy(amount) {
  let tokenAddress = $(this).attr("tokenAddress");
  let rainbowWallToken = new web3.eth.Contract(
    RainbowWallTokenABI,
    tokenAddress
  );
  rainbowWallToken.methods.buy(selectedAccount).send({ value: amount });
}

async function sell(amount) {
  let tokenAddress = $(this).attr("tokenAddress");
  let rainbowWallToken = new web3.eth.Contract(
    RainbowWallTokenABI,
    tokenAddress
  );
  rainbowWallToken.methods.sell(selectedAccount, amount);
}

async function getMetadatas() {
  let totalSupply = await rainbowWallProtocol.methods.totalSupply().call();
  if (totalSupply > 0)
    for (const index of generateArray(0, totalSupply - 1).reverse()) {
      await appendContent(index + 1);
    }
}

