function hasBorther(va1) {
  if (va1.next()[0]) {
    return { bor: va1.next() };
  } else {
    return { par: va1.parent() };
  }
}

function removeDiv(app, Div) {
  if (app.bor) {
    Div.insertBefore(app.bor);
  } else {
    Div.appendTo(app.par);
  }
}

